import React, { useState } from "react";
import "./Contents.css";

export default function Contents() {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [pwValid, setPwValid] = useState(false)

  const handleIdChange = (e) => {
    setId(e.target.value);
  };

  const handlePwChange = (e) => {
    setPw(e.target.value);
    const regex = /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,15}$/
    if(regex.test(pw)) {
        setPwValid(true);
    } else {
        setPwValid(false);
    }
  }

  return (
    <div className="form">
      <div className="sign-up">회원가입</div>
      <div className="id">
        아이디 <br />
        <input
          className="idInput"
          type="text"
          maxLength={15}
          placeholder="abcd1234"
          value={id}
          onChange={handleIdChange}
        />
      </div>
      <div className="errorId">
        {

        }
      </div>

      <div className="password">
        비밀번호 <br />
        <input
          className="passwordInput"
          type="password"
          placeholder="영문, 숫자, 특수문자 조합 8-16자"
          value={pw}
          onChange={handlePwChange}
        />
      </div>
      <div className="errorPw">비밀번호를 정확히 입력해주세요.</div>

      <div className="pwCheck">
        비밀번호 확인 <br />
        <input className="pwCheckInput" type="password" />
      </div>
      <div className="errorPwCheck">비밀번호가 다릅니다.</div>

      <div className="phoneNumber">
        휴대폰 번호 <br />
        <input className="phoneNumberInput" placeholder="01012341234" />
      </div>

      <div className="address">
        주소 <br />
        <input className="addressInput" />
      </div>

      <div className="email">
        이메일 <br />
        <input className="emailInput" placeholder="abc123@gmail.com" />
      </div>

      <div className="agree">
        [필수]만 14세 이상이며 모두 동의합니다
        <input type="checkbox" className="agreeInput" />
      </div>

      <div>
        <button>가입하기</button>
      </div>
    </div>
  );
}
