  return (
    <div className="form">
      <div className="sign-up">회원가입</div>
      <IdForm />
      <PwForm />
      <PhoneForm />
      <AddressForm />
      <EmailForm />
      <CheckBoxForm />

      <div className="btnBackground">
        <button className="bottomBtnText" type="submit" disabled={finalBtn}>
          가입하기
        </button>
      </div>
    </div>
  );
}
